// Generated by CoffeeScript 1.9.3
(function () {
    define('demo/dataTree/ExpandModule', [
        'angular',
        'framework/datasource/DataSourceModule',
        'framework/datatree/GillionDataTreeModule'
    ], function (angular, GillionDataTreeModule) {
        var ExpandModule = angular.module('ExpandModule', ['GillionDataTreeModule']);
        ExpandModule.controller('ExpandController', function ($scope, $dataTreeManager) {

            //加载完成后设置勾选
            $scope.loadSuccess = function () {
                //设置ngModel的值
                $scope.checkValues = "id1,id4";
            };

            //展开已勾选结点
            $scope.expandAllCheckedItem = function () {
                var dataTree = $dataTreeManager.getDataTree("expandCheckTree");
                var checkedItemArray = $scope.checkValues.split(",");
                angular.forEach(checkedItemArray, function (idProp) {
                    //expandNode(idProp, expand, isChildren)
                    ///idProp 结点id
                    //expand  true展开，false收缩
                    //isChildren 级联children
                    dataTree.expandNode(idProp, true, true);
                });
            };

        });
        return ExpandModule;
    })
})(window);



